INSERT INTO `cn_content_pic` VALUES('18','12','&lt;p style=&quot;text-align: center; line-height: 2em;&quot;&gt;&lt;span style=&quot;font-size:19px&quot;&gt;&lt;img src=&quot;/data/upload/image/20200328/1585410113649370.jpg&quot; title=&quot;1585410113649370.jpg&quot; alt=&quot;1585410113649370.jpg&quot; width=&quot;500&quot; height=&quot;767&quot; border=&quot;0&quot; vspace=&quot;0&quot; style=&quot;width: 500px; height: 767px;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center; line-height: 2em;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size:19px&quot;&gt;法拉古特东莞学习中心执行校长&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; line-height: 2em;&quot;&gt;&lt;span style=&quot;font-size:19px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; line-height: 3em;&quot;&gt;&lt;span style=&quot;font-size:19px&quot;&gt;澳洲昆士兰大学双学士，格里菲斯大学荣誉硕士。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; line-height: 3em;&quot;&gt;&lt;span style=&quot;font-size:19px&quot;&gt;英国剑桥大学&lt;/span&gt;&lt;span style=&quot;font-size:19px;font-family:&amp;#39;Helvetica Neue&amp;#39;,serif&quot;&gt; CLET-P,CLET-S&lt;/span&gt;&lt;span style=&quot;font-size:19px&quot;&gt;，官方认证指定全国首批中、小学教师培训师，担任各学校的教师培训；北京外国语大学特聘讲师。投身教育行业十多年，为数千组家庭做过国际教育生涯规划。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','Ade','','');
